mapper.select:
select user_id, dataset_name, current_index, dataset_sub_set, create_time, update_time
from user_annotation_info
where user_id = #{userId}

select user_id, dataset_name, current_index, dataset_sub_set, create_time, update_time
from user_annotation_info
where user_id = #{userId} 
and dataset_name = #{datasetName} 
and dataset_sub_set = #{datasetSubSet}

mapper.update:
update user_annotation_info
set current_index = #{currentIndex},
    update_time = CURRENT_TIMESTAMP
where user_id = #{userId}
and dataset_name = #{datasetName} 
and dataset_sub_set = #{datasetSubSet}

mapper.insert:
insert into user_annotation_info (
    user_id, dataset_name, current_index, dataset_sub_set, create_time, update_time
) values (
    #{userId}, #{datasetName}, #{currentIndex}, #{datasetSubSet}, 
    CURRENT_TIMESTAMP, CURRENT_TIMESTAMP
)
